import numpy as np
import time
def p_test(test):
    flag = 0
    for num in test:
        flag = flag + num
        if flag < 0:
            return False
    if flag != 0:
        return False
    return True

def longestValidParentheses(s: str) -> int:
    if s == "" or s == "(" or s == ")" or s == ")(":
            return 0
    np_test_list = np.array([], dtype = "int8")
    left_counter = 0
    right_counter = 0
    maxi = 0
    for i in range(len(s)):
        if s[i] == '(':
            np_test_list = np.append(np_test_list, 1)
            left_counter += 1
        else: 
            np_test_list = np.append(np_test_list, -1)
            right_counter += 1
    if left_counter < right_counter:
        maxi = left_counter
    elif left_counter > right_counter:
        maxi = right_counter
            
    for j in range(len(np_test_list)-(maxi * 2), len(np_test_list)):
        for k in range(0,j+1):
            if len(np_test_list[k:len(np_test_list)-j+k]) % 2 == 1:
                break
            if p_test(np_test_list[k:len(np_test_list)-j+k]) == True:
                return len((np_test_list[k:len(np_test_list)-j+k]))
    return 0        

